isDateToday: function(crtDate) {
	var currentDate = new GlideDateTime();
    var today = currentDate.getLocalDate();  
	var testdDate = new GlideDateTime(crtDate);
	var isToday = testdDate.getLocalDate();
    if (today.compareTo(isToday) == 0) {
        return true;
    } else {
        return false;
    }
},



    type: 'NeedItUtils'
};
